import React, { useState } from "react";
import Layout from "../../layout/Layout/Layout";
import classes from "./Banner.module.scss";
import Line from "./Line/Line";
const content = [
  {
    title: "Mục tiêu mà G-STORE đang tạo ra là gì",
    content:
      "Với mục tiêu: MINH BẠCH, NHIỆT HUYẾT, TẬN TÂM, AN TOÀN. G-Store sẽ là sự tập hợp, kết nối các nguồn lực toàn cầu nhằm mục đích xây dựng các sản phẩm, dịch vụ với nền tảng công nghệ 4.0 tạo ra lợi ích cho cộng đồng và xã hội",
  },
  {
    title: "Tầm nhìn của G-STORE là gì",
    content:
      "Tầm nhìn: Trở thành công ty công nghệ nằm trong top khu vực châu Á trong 5 năm tới, với cốt lõi là giải pháp chia sẻ hàng hóa, dịch vụ trong xã hội, tạo kết nối nhu cầu và khả năng cung cấp sản phẩm, dịch vụ trong lĩnh vực trao đổi hàng hóa, dịch vụ thông qua nền tảng công nghệ 4.0. Khẳng định lại giá trị của nền kinh tế chia sẻ trong cộng đồng. Tạo ra nền tảng kết nối các giá trị đích thực và đặt giá trị cộng đồng lên ưu tiên cao nhất.",
  },
];
const Banner = () => {
  const [isTouched, setIsTouched] = useState(false);
  const touchedHandler = () => {
    setIsTouched((prevState) => !prevState);
  };
  return (
    <Layout className={classes.container}>
      <h2>Về chúng tôi</h2>
      <p className={classes.title}>
        G-Store luôn nỗ lực hàng ngày nhằm mang lại những giá trị vượt bậc, sự
        tiện lợi, nhanh chóng cho mọi khách hàng, cộng đồng sử dụng ứng dụng
        G-Store
        <br /> ngay cả đến những khách hàng khó tính nhất.
      </p>
      <Layout className={classes["content-box"]}>
        <Line isTouched={isTouched} onToggle={touchedHandler} />
      </Layout>
    </Layout>
  );
};

export default Banner;
