import React, { useEffect, useState } from "react";
import BlogComponent from "./BlogComponent";
import Layout from "../Layout/Layout";
import SearchField from "./SearchField";
import Container from "../container/Container";
import { Row, Col } from "react-bootstrap";
import useHttp from "../../hook/use-http";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { Helmet } from "react-helmet";
import styles from "./BlogDashboard.module.scss";
import Loading from "../../Loading/Loading";
import RemoveUnicode from "../../RemoveUnicode/RemoveUnicode";
const BlogDashboard = () => {
  const { fetchingDataHandler, data, status } = useHttp();
  const [title, setTitle] = useState('');
  useEffect(() => {
    fetchingDataHandler({
      url: "http://localhost:3001/get-all-posts",
    });
  }, [fetchingDataHandler]);
  const changeHandler = (event) =>{
    setTitle(event.target.value);
  }
  return (
    <>
      <Helmet>
        <title>Tin Tức - Dashboard Admin - GStoreVN</title>
      </Helmet>
      <Layout title="Tin Tức Dashboard">
        {status === "pending" ? (
          <Loading />
        ) : (
          <Container>
            <SearchField input={{
              placeholder: 'Search...',
              onChange: changeHandler
            }}>
              <FontAwesomeIcon icon={faSearch} />
            </SearchField>
            <Row>
              {data &&
                data.length > 0 &&
                data.map((items) => {
                  const transformLink = RemoveUnicode(items.title);
                  return (
                    <Col xs={12} sm={12} md={6} lg={4} className={styles.column}>
                      <BlogComponent
                        key={items.id}
                        link={transformLink}
                        title={items.title}
                        date={items.dateblog}
                        url={
                          require(`../../../img/images/${items.image1}`).default
                        }
                      />
                    </Col>
                  );
                })}
            </Row>
          </Container>
        )}
      </Layout>
    </>
  );
};

export default BlogDashboard;
