import React, {useRef} from "react";
import Layout from "../../layout/Layout/Layout";
import styles from "./Download.module.scss";
import { Row, Col } from "react-bootstrap";
import Button from '@material-ui/core/Button';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faApple, faAndroid} from '@fortawesome/free-brands-svg-icons';
import p1 from '../../img/images/Application.png';
const Download = () => {
    const btnRef = useRef();
    const buttonHandler = () =>{
        console.log(btnRef);        
    }
  return (
    <div className={styles.container}>
      <Layout>
          <Row className={styles.row}>
              <Col xs={12} sm={12} md={6} lg={6} className={styles['left-side']}>
                <h2>Trải nghiệm ngay ứng dụng G-STORE</h2>
                <p>Tải ứng dụng và tham gia mua - bán với cộng đồng G-STORE ngay</p>
                <div className={styles.button}>
                    <Button ref={btnRef} onClick={buttonHandler} variant='contained' className={`${styles.btn} ${styles['apple-btn']}`}>
                        <FontAwesomeIcon icon={faApple}/>
                        <span>Tải trên Appstore</span>
                    </Button>
                    <Button variant='contained' className={`${styles.btn} ${styles['andr-btn']}`}>
                        <FontAwesomeIcon icon={faAndroid}/>
                        <span>Tải trên CHPlay</span>
                    </Button>
                </div>
              </Col>
              <Col xs={12} sm={12} md={5} lg={5}>
                <img src={p1} alt=''/>
              </Col>
          </Row>
      </Layout>
    </div>
  );
};

export default Download;
