import React from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/swiper.min.css";
import "swiper/components/pagination/pagination.min.css";
import 'swiper/components/navigation/navigation.scss';
// import Swiper core and required modules
import SwiperCore, { Pagination, Autoplay, Navigation } from "swiper/core";
// install Swiper modules
SwiperCore.use([Pagination, Autoplay, Navigation]);
const ScreenData = [
  "../../img/images/Screen/0.png",
  "../../img/images/Screen/1.png",
  "../../img/images/Screen/2.png",
  "../../img/images/Screen/3.png",
  "../../img/images/Screen/4.png",
  "../../img/images/Screen/5.png",
  "../../img/images/Screen/6.png",
  "../../img/images/Screen/7.png",
  "../../img/images/Screen/8.png",
  "../../img/images/Screen/9.png",
  "../../img/images/Screen/10.png",
  "../../img/images/Screen/11.png",
  "../../img/images/Screen/12.png",
  "../../img/images/Screen/13.png",
  "../../img/images/Screen/14.png",
  '../../img/images/Screen/15.png'
];
const Screen = () => {
  const renderSlider = ScreenData.map((items, index) =>{
    return <SwiperSlide key={index}>
      <img src={require(items).default} alt=''/>
    </SwiperSlide>
  })
  return (
    <>
      <Swiper
        slidesPerView={3}
        spaceBetween={30}
        autoplay={{
          delay: 5000,
        }}
        navigation={true}
        pagination={{
          clickable: true,
        }}
      >
        {renderSlider}
      </Swiper>
    </>
  );
};

export default Screen;
