import React, {useEffect} from "react";
import styles from "./FieldSet.module.scss";
import TextArea from "./Input/TextArea";
import useHttp from "../../../hook/use-http";
import { useLocation } from "react-router-dom";
const FieldSet = () => {
  const {fetchingDataHandler, data, error, status} = useHttp();
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const id = params.get('id');
  useEffect(() =>{
    fetchingDataHandler({
      url: `http://localhost:3001/get-post/${id}`
    })
  }, [fetchingDataHandler])
  return (
    <div className={styles.form}>
      <p>{status}</p>
      <TextArea
        textField={{
          variant: "outlined",
          label: "Tiêu Đề",
          placeholder: "Tiêu đề trang",
          multiline: true,

        }}
      />
      <TextArea
        textField={{
          label: "Nội Dung 1",
          placeholder: "Nội dung trong trang",
          multiline: "multiline",
          variant: "outlined",
          multiline: true,
        }}
      />
      <TextArea
        textField={{
          variant: "outlined",
          label: "Nội Dung 2",
          placeholder: "Nội dung trong trang",
          multiline: "multiline",
          multiline: true,
        }}
      />
      <TextArea textField={{
        variant: 'outlined',
        label: 'Nội Dung 3',
        placeholder: 'Nội dung trong trang',
        multiline: 'multiline',
        multiline: true,
      }}/>
    </div>
  );
};

export default FieldSet;
