import React from "react";
import Layout from "../../layout/Layout/Layout";
import styles from "../scss/Form.module.scss";
import Input from "../../UI/Input";
import { Button } from "@material-ui/core";
import useInput from "../hook/use-input";
const RegisterPage = () => {
  const {
    valueInput: emailInput,
    changeInputHandler: changeEmailHandler,
    isTouched: emailIsTouched,
    inputIsTouchedHandler: emailIsTouchedHandler,
    isValid: emailIsValid,
  } = useInput(
    (valueEmail) => valueEmail.trim().length > 0 && valueEmail.includes("@")
  );
  const {valueInput: passwordInput, changeInputHandler: passwordChangeHandler, isTouched: passwordIsTouched, } = useInput(valuePassword.trim().length >= 8)
  return (
    <form className={styles.form}>
      <Layout className={styles.layout}>
      <Input
          input={{
            type: "text",
            id: "name",
            label: "Name",
            placeholder: "Tên...",
            value: emailInput,
            onChange: changeEmailHandler,
            onBlur: emailIsTouchedHandler
          }}
        />
        <Input
          input={{
            type: "email",
            id: "email",
            label: "Email",
            placeholder: "Email...",
            value: emailInput,
            onChange: changeEmailHandler,
            onBlur: emailIsTouchedHandler
          }}
        />
        {(emailIsTouched && !emailIsValid) && <p className={styles.invalid}>Email Không Hợp Lệ</p>}
        <Input
          input={{
            type: "password",
            id: "password",
            label: "Password",
            placeholder: "Password...",
          }}
        />
        <div className={`${styles.submit}`}>
          <Button variant="contained" type={"submit"}>
            Đăng nhập
          </Button>
        </div>
      </Layout>
    </form>
  );
};

export default RegisterPage;
