import { createSlice, configureStore } from "@reduxjs/toolkit";
const initialStateLayout = {
  isClicked: false,
  isDownloaded: false,
};

const layoutSlice = createSlice({
  name: "layout",
  initialState: initialStateLayout,
  reducers: {
    toggleLayout(state) {
      state.isClicked = !state.isClicked;
    },
    toggleDownload(state) {
      state.isDownloaded = !state.isDownloaded;
    },
  },
});

const initialStateSignIn = {
  isSignedIn: false,
  information: {},
  id: null
};
const signInSlice = createSlice({
  name: "signin",
  initialState: initialStateSignIn,
  reducers: {
    SignIn(state, action) {
      state.isSignedIn = true;
      state.id = action.payload
    },
    SignOut(state) {
      state.isSignedIn = false;
    },
    Information(state, action){
      state.information = action.payload
    },
  },
});

const adminInformation = () =>{
  return (dispatch) =>{
    if(initialStateSignIn.id === null){
      return;
    }
    fetch(`http://localhost:3001/admin/${id}`)
  }
}
export const layoutActions = layoutSlice.actions;
export const signInActions = signInSlice.actions;
const store = configureStore({
  reducer: {
    layout: layoutSlice.reducer,
    signin: signInSlice.reducer,
  },
});

export default store;
