import React, {useRef, useState} from "react";
import Layout from "../../layout/Layout/Layout";
import styles from "./Download.module.scss";
import { Row, Col } from "react-bootstrap";
import Button from '@material-ui/core/Button';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faApple, faAndroid} from '@fortawesome/free-brands-svg-icons';
import p1 from '../../img/images/Application.png';
import { createPortal } from 'react-dom';
import Overlay from "../Overlay/Overlay";
import DownloadNotification from "./DownloadNotification";
const Download = () => {
    const [isClicked, setIsClicked] = useState(false);
    const buttonRef = useRef();
    const buttonHandler = (data) =>{    
        console.log(data);
        setIsClicked(prevState => !prevState);
    }
  return (
    <div className={styles.container}>
      <Layout aos='fade-up'>
          <Row className={styles.row}>
              <Col xs={12} sm={12} md={6} lg={6} className={styles['left-side']}>
                <h2>Trải nghiệm ngay ứng dụng G-STORE</h2>
                <p>Tải ứng dụng và tham gia mua - bán với cộng đồng G-STORE ngay</p>
                <div className={styles.button}>
                    <Button ref={buttonRef} tabIndex='0' onClick={() => buttonHandler(0)} variant='contained' className={`${styles.btn} ${styles['apple-btn']}`}>
                        <FontAwesomeIcon icon={faApple}/>
                        <span>Tải trên Appstore</span>
                    </Button>
                    <Button ref={buttonRef} onClick={() => buttonHandler(1)} tabIndex='1' variant='contained' className={`${styles.btn} ${styles['andr-btn']}`}>
                        <FontAwesomeIcon icon={faAndroid}/>
                        <span>Tải trên CHPlay</span>
                    </Button>
                </div>
              </Col>
              <Col xs={12} sm={12} md={5} lg={5}>
                <img src={p1} alt=''/>
              </Col>
          </Row>
          <DownloadNotification/>
      </Layout>
      {isClicked && createPortal(<Overlay/>, document.getElementById('overlay'))}
    </div>
  );
};

export default Download;
