import React from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";
import styles from "./Screen.module.scss";
// Import Swiper styles
import "swiper/swiper.min.css";
import "swiper/components/pagination/pagination.min.css";
import "swiper/components/navigation/navigation.scss";
// import Swiper core and required modules
import SwiperCore, { Pagination, Autoplay, Navigation } from "swiper/core";
// install Swiper modules
SwiperCore.use([Pagination, Autoplay, Navigation]);

const ScreenData = [
  "1.PNG",
  "2.PNG",
  "3.PNG",
  "4.PNG",
  "5.PNG",
  "6.PNG",
  "7.PNG",
  "8.PNG",
  "9.PNG",
  "10.PNG",
  "11.PNG",
  "12.PNG",
  "13.PNG",
];
const test = ['0.png', '1.png', '2.png', '3.png'];
const Screen = () => {
  const renderSlider = test.map((items, index) => {
    return (
      <SwiperSlide className={styles.images} key={index}>
        <img
          // src={require(`../../img/images/ScreenApp/${items}`).default}
          src={`${process.env.PUBLIC_URL}/Screen/${items}`}
          alt=""
          loading='lazy'
        />
      </SwiperSlide>
    );
  });
  return (
    <>
      <Swiper
        className={styles.slider}
        spaceBetween={30}
        autoplay={{
          delay: 5000,
        }}
        loop={true}
        breakpoints={{
          200: {
            slidesPerView: 2,
          },
          576: {
            slidesPerView: 3,
            spaceBetween: 10,
          },
          772: {
            slidesPerView: 4,
            spaceBetween: 20,
          },
          1200: {
            slidesPerView: 5,
            spaceBetween: 25,
          },
        }}
        navigation={true}
        pagination={{
          clickable: true,
        }}
        grabCursor
        onPaginationRender={(swiper, el) => {
          el.classList.add(styles.pagination);
        }}
      >
        {renderSlider}
      </Swiper>
    </>
  );
};

export default Screen;
