import React, { useEffect } from "react";
import AOS from "aos";
import "bootstrap/dist/css/bootstrap.min.css";
import Footer from "./components/Footer/Footer";
import { Switch, Route, Redirect, useLocation } from "react-router-dom";
import Home from "./components/Pages/Home";
import Blog from "./components/Pages/Blog";
import { useSelector, useDispatch } from "react-redux";
import { layoutActions } from "./components/redux-store/Store";
import LinkNavigation from "./components/Navigation/LinkNavigation";
import Signin from "./components/Pages/Signin";
import FeaturePage from "./components/Pages/FeaturePage";
const App = () => {
  useEffect(() => {
    AOS.init({
      once: true,
      duration: 1000,
      offset: 120,
      delay: 200,
    });
  }, []);
  const location = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [location.pathname]);
  const dispatch = useDispatch();
  const isClicked = useSelector((state) => state.layout.isClicked);
  const toggleHandler = () => {
    dispatch(layoutActions.toggleLayout());
  };
  return (
    <>
      <LinkNavigation toggle={toggleHandler} transform={isClicked} />
      <Switch>
        <Route path="/" exact component={Home} />
        <Route path="/admin" exact>
          <Home />
        </Route>
        <Route path="/tinh-nang" component={FeaturePage} />
        <Route path=''/>
        <Route path="/admin/dang-nhap">
          <Signin />
        </Route>
        <Route path="/tin-tuc">
          <Blog />
        </Route>
        <Route path="*">
          <Redirect to="/" />
        </Route>
      </Switch>
      <Footer />
    </>
  );
};

export default App;
