import React, { useEffect, useState, useReducer } from "react";
import Layout from "../Layout/Layout";
import Input from "../UI/Input/Input";
import styles from "./UserDetail.module.scss";
import { Row, Col } from "react-bootstrap";
import ToggleButton from "../ChangingPost/UI/ToggleButton/ToggleButton";
import { Button } from "@material-ui/core";
import Container from "../container/Container";
import Badge from "../Badge/Badge";
import { useParams } from "react-router-dom";
import Loading from "../../../UI/Loading/Loading";
const initialState = {
  user: null,
  status: "pending",
};
const reducerFn = (state, action) => {
  switch (action.type) {
    case "LOADING":
      return {
        ...state,
        status: "pending",
      };
    case "COMPLETED":
      return {
        status: "completed",
        user: action.payload,
      };
    case "ERROR":
      return {
        ...state,
        status: "error",
      };
    case 'CHANGE_USERNAME':
        return {
        }
    default:
      return initialState;
  }
};
const UserDetail = () => {
  const params = useParams();
  useEffect(() => {
    const fetchUser = async () => {
      try {
        dispatch({
          type: "LOADING",
        });
        const response = await fetch(
          `http://localhost:3001/api/get-user/${params.username}`
        );
        if (!response.ok) {
          throw new Error("failed to fetch");
        }
        const dataUser = await response.json();
        dispatch({
          type: "COMPLETED",
          payload: dataUser,
        });
      } catch (err) {
        dispatch({
          type: "ERROR",
        });
      }
    };
    fetchUser();
  }, [params.username]);
  console.log(state.user);
  const changeUserNameHandler = event =>{

  }
  const changeEmailHandler = event => {

  }
  const changePasswordHandler = event => {

  }
  return (
    <Layout title="Chỉnh Sửa Người Dùng">
      {state.status === "pending" && <Loading className={styles.loading}/>}
      {state.status === "completed" && state.user && (
        <form>
          <Row>
            <Col xs={12} sm={12} md={8} lg={8}>
              <Container>
                <Input
                  className={styles.input}
                  label="Tên người dùng"
                  input={{
                    type: "text",
                    required: true,
                    id: "name",
                    max: "100",
                    value: state.user.name,
                    onChange: changeUserNameHandler
                  }}
                />
                <Input
                  className={styles.input}
                  label="Email"
                  input={{
                    type: "email",
                    required: true,
                    id: "email",
                    value: state.user.username,
                    onChange: changeEmailHandler
                  }}
                />
                <Input
                  className={styles.input}
                  label="Mật khẩu mới"
                  input={{
                    type: "password",
                    required: true,
                    id: "password",
                    min: "8",
                    onChange: changePasswordHandler,
                    placeholder: 'Mật khẩu mới (Nhập mật khẩu cũ nếu không thay đổi)'
                  }}
                />
              </Container>
            </Col>
            <Col className={styles.col} xs={12} sm={12} md={4} lg={4}>
              <Container>
                <p>Trạng thái tài khoản</p>
                <div className={styles["row__lock"]}>
                  <ToggleButton />
                  <Badge />
                </div>
                <Button variant="contained" type="submit">
                  Xác nhận
                </Button>
              </Container>
            </Col>
          </Row>
        </form>
      )}
    </Layout>
  );
};

export default UserDetail;
