import React, { useEffect } from "react";
import Container from "../container/Container";
import Layout from "../Layout/Layout";
import useHttp from "../../hook/use-http";
import Loading from "../../../UI/Loading/Loading";
import styles from "./Users.module.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faEye, faTrashAlt} from '@fortawesome/free-regular-svg-icons';
const Users = () => {
  const { fetchingDataHandler, status, data, error } = useHttp();
  useEffect(() => {
    fetchingDataHandler({
      url: "http://localhost:3001/api/users",
    });
  }, [fetchingDataHandler]);
  console.log(data);
  return (
    <>
      <Layout>
        <Container>
          {status === "pending" && <Loading className={styles.loading} />}
          {status === "completed" && data && (
            <div className={styles.table}>
              <table className={styles["user-tb"]}>
                <tbody>
                  <tr>
                    <th>Email đăng nhập</th>
                    <th>Tên</th>
                  </tr>
                  <tr>
                      <td>ABC@gmail.com</td>
                      <td>Chris</td>
                      <td className={styles.row}>
                          <span className={styles.btn}><FontAwesomeIcon icon={faEye}/></span>
                          <span className={`${styles.btn} ${styles.delete}`}><FontAwesomeIcon icon={faTrashAlt}/></span>
                      </td>
                  </tr>

                </tbody>
              </table>
            </div>
          )}
        </Container>
      </Layout>
    </>
  );
};

export default Users;
