import React, {useEffect, useState} from 'react';
import styles from '../scss/LinkNavigation.module.scss';
import logo from '../../img/images/logo3.png';
import logoFixed from '../../img/images/logo-2.png';
const link = ['Trang chủ', 'Tính năng', 'Thống kê', 'Màn hình', 'Về chúng tôi', 'Tin tức', 'Blog'];
const tag = ['home', 'features', 'stats', 'team', 'testimonial', 'posts', 'blogs'];
const LinkNavigation = ({toggle, transform}) =>{
    const [fixed, setFixed] = useState(false);
    useEffect(() =>{
        const scrollFunction = () =>{
            if(window.pageYOffset > 200){
                setFixed(true);
            }
            else{
                setFixed(false)
            }
        }
        window.addEventListener('scroll', scrollFunction)
    }, [])
    return(
        <nav className={`${styles['nav-bar']} ${fixed && styles.fixed}`}>
            <div className={styles.logo}>
                <img src={fixed ? logoFixed : logo} alt='logo'/>
            </div>
            <ul className={`${transform ? styles['nav-bar-block'] : ''}`}>
                {link.map((items, index) =>{
                    return <li key={index}><a href={`#${tag[index]}`}>{items}</a></li>
                })}
            </ul>
            <div onClick={toggle} className={`${styles.hamburger} ${transform && styles['hamburger-transition']}`}>
                <div></div>
                <div></div>
                <div></div>
            </div>
        </nav>
    )
}

export default LinkNavigation;
