import React, {useEffect, useState} from "react";
import styles from "./FieldSet.module.scss";
import TextArea from "./Input/TextArea";
import useHttp from "../../../hook/use-http";
import { useLocation } from "react-router-dom";
import Loading from "../../../../UI/Loading/Loading";
const FieldSet = () => {
  const {fetchingDataHandler, data, error, status} = useHttp();
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const id = params.get('id');
  useEffect(() =>{
    fetchingDataHandler({
      url: `http://localhost:3001/get-post/${id}`
    })
  }, [fetchingDataHandler, id]);

  return (
    <>
    {status === 'pending' && <Loading className={styles['loading-spinner']}/>}

    <div className={styles.form}>
      <TextArea
        textField={{
          variant: "outlined",
          label: "Tiêu Đề",
          placeholder: "Tiêu đề trang",
          multiline: true,
        }}
      />
      <TextArea
        textField={{
          label: "Nội Dung 1",
          placeholder: "Nội dung trong trang",
          variant: "outlined",
          multiline: true,
        }}
      />
      <TextArea
        textField={{
          variant: "outlined",
          label: "Nội Dung 2",
          placeholder: "Nội dung trong trang",
          multiline: true,
        }}
      />
      <TextArea textField={{
        variant: 'outlined',
        label: 'Nội Dung 3',
        placeholder: 'Nội dung trong trang',
        multiline: true,
      }}/>
    </div>
    </>
  );
};

export default FieldSet;
