import React, { useEffect, useState } from "react";
import styles from "./FieldSet.module.scss";
import TextArea from "./Input/TextArea";
import { useLocation } from "react-router-dom";
import Dropzone from "./Dropzone/Dropzone";
import { useDispatch, useSelector } from "react-redux";
import { fetchingPosts } from "../../../redux-store/change-post";
import Loading from "../../../../UI/Loading/Loading";
const FieldSet = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const id = params.get("id");
  const status = useSelector(state => state.post.status);
  const data = useSelector(state => state.post.information);
  const dispatch = useDispatch();
  useEffect(() =>{
    dispatch(fetchingPosts(id));
  }, [id, dispatch])
  console.log(data);
  console.log(status);
  return (
    <>
      {status === 'pending' && !data && <Loading className={styles['loading-spinner']}/>}
      {status === 'error' && !data && <p></p>}
        <div className={styles.form}>
          <TextArea
            textField={{
              variant: "outlined",
              label: "Tiêu Đề",
              placeholder: "Tiêu đề trang",
              multiline: true,
            }}
          />
          <TextArea
            textField={{
              label: "Nội Dung 1",
              placeholder: "Nội dung trong trang",
              variant: "outlined",
              multiline: true,
            }}
          />
          <TextArea
            textField={{
              variant: "outlined",
              label: "Nội Dung 2",
              placeholder: "Nội dung trong trang",
              multiline: true,
            }}
          />
          <TextArea
            textField={{
              variant: "outlined",
              label: "Nội Dung 3",
              placeholder: "Nội dung trong trang",
              multiline: true,
            }}
          />
        <div>
          <Dropzone/>
        </div>
        </div>
    </>
  );
};

export default FieldSet;
