import React, { useEffect, useRef } from "react";
import styles from "./FieldSet.module.scss";
import TextArea from "./Input/TextArea";
import { useLocation, Redirect } from "react-router-dom";
import Dropzone from "./Dropzone/Dropzone";
import { useDispatch, useSelector } from "react-redux";
import { fetchingPosts } from "../../../redux-store/change-post";
import Loading from "../../../../UI/Loading/Loading";
import { changePostActions } from "../../../redux-store/change-post";
const FieldSet = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const id = params.get("id");
  const status = useSelector((state) => state.post.status);
  const data = useSelector((state) => state.post.information);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchingPosts(id));
  }, [id, dispatch]);
  console.log(data);
  const changeTitleHandler = e =>{
    dispatch(changePostActions.updateTitle(e.target.value));
  }
  return (
    <>
      {status === "pending" && !data && (
        <Loading className={styles["loading-spinner"]} />
      )}
      {status === "error" && !data && <Redirect to="/404" />}
      {status === "success" && data && (
        <div className={styles.form}>
          <TextArea
            textField={{
              variant: "outlined",
              label: "Tiêu Đề",
              placeholder: "Tiêu đề trang",
              multiline: true,
              defaultValue: data.title,
            }}
          />
          <TextArea
            textField={{
              label: "Nội Dung 1",
              placeholder: "Nội dung trong trang",
              variant: "outlined",
              multiline: true,
              defaultValue: data.content1,
            }}
          />
          <TextArea
            textField={{
              variant: "outlined",
              label: "Nội Dung 2",
              placeholder: "Nội dung trong trang",
              multiline: true,
              defaultValue: data.content2
            }}
          />
          <TextArea
            textField={{
              variant: "outlined",
              label: "Nội Dung 3",
              placeholder: "Nội dung trong trang",
              multiline: true,
              defaultValue: data.content3
            }}
          />
          <div>
            <Dropzone />
          </div>
        </div>
      )}
    </>
  );
};

export default FieldSet;
