import React from "react";
import Layout from "../Layout/Layout";
import Container from "../helper/Container/Container";
import { TextField } from "@material-ui/core";
import styles from "./FormPost.module.scss";
import useInput from "../../hook/use-input";
import Files from "./Files";
const FormPost = () => {
  const {
    isValid,
    isTouched,
    inputIsTouchedHandler,
    changeInputHandler,
    valueInput,
  } = useInput((value) => value.trim().length > 0);
  return (
    <Layout title="Tạo Blog">
      <Container className={styles.container}>
        <form className={styles.form}>
          <label htmlFor="title">Tiêu đề bài viết</label>
          <TextField
            id="title"
            margin="normal"
            required={true}
            label="Tiêu đề"
            variant="outlined"
            multiline
            placeholder="Tiêu Đề"
            size="medium"
            fullWidth={true}
            value={valueInput}
            onChange={changeInputHandler}
            onBlur={inputIsTouchedHandler}
            error={!isValid && isTouched}
          />
          <label htmlFor="body-1">Đoạn 1</label>
          <TextField
            id="body-1"
            margin="normal"
            label="Đoạn 1"
            variant="outlined"
            multiline
            placeholder="Nội Dung 1"
            size="medium"
            fullWidth={true}
          />
          <label htmlFor="body-2">Đoạn 2</label>
          <TextField
            id="body-2"
            margin="normal"
            label="Đoạn 2"
            variant="outlined"
            multiline
            placeholder="Nội Dung 2"
            size="medium"
            fullWidth={true}
          />
          <label htmlFor="body-3">Đoạn 3</label>
          <TextField
            id="body-3"
            margin="normal"
            label="Đoạn 3"
            variant="outlined"
            multiline
            placeholder="Nội Dung 3"
            size="medium"
            fullWidth={true}
          />
          <Files />
        </form>
      </Container>
    </Layout>
  );
};

export default FormPost;
