import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  status: "pending",
  posts: [],
  error: null,
};

const postsSlice = createSlice({
  name: "get-posts",
  initialState: initialState,
  reducers: {
    getAllPostsHandler(state, action) {
      (state.posts = action.payload), (state.status = "completed");
    },
    errorHandler(state, action) {
      state.error = action.payload;
    },
    pendingHandler(state) {
      state.status = "pending";
    },
  },
});

export const adminInformation = () =>{
    return (dispatch) =>{
      const id = localStorage.getItem('isSignedIn');
      if(!id){
        console.log('no id!')
        return;
      }
      fetch(`http://localhost:3001/admin/${JSON.parse(id)}`).then(response => response.json())
      .then(data =>{
        dispatch(signInActions.Information(data));
      }).catch(err => console.log(err));
    }
  }
export const postsActions = postsSlice.actions;
export default postsSlice;
