import React, { useEffect } from "react";
import styles from "../scss/NewestPost.module.scss";
import { Row, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import BlogComponent from "./BlogComponent";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCalendarAlt } from "@fortawesome/free-regular-svg-icons";
import { useRouteMatch } from "react-router-dom";
import RecentPost from "../RecentPost/RecentPost";
import useHttp from "../hook/use-http";
const NewestPost = () => {
  const route = useRouteMatch();
  const { fetchingDataHandler, data, status } = useHttp();
  useEffect(() => {
    fetchingDataHandler({
      url: "http://localhost:3001/get-all-posts",
    });
  }, [fetchingDataHandler]);

  return (
    <>
      {data &&
        data.length > 0 &&
        data.map((items, index) => {
          if (index === 0) {
            return (
              <Row className={styles.row}>
                <Col
                  xs={12}
                  sm={12}
                  md={12}
                  lg={12}
                  className={`${styles.col} ${styles.newest}`}
                >
                  <img
                    src={require("../../img/images/banner-cut.png").default}
                    alt=""
                  />
                  <div className={styles.content}>
                    <Link
                      to={`${route.path}/g-store-thoi-diem-vang-de-day-manh-kinh-doanh-online?id=1`}
                    >
                      G-STORE – THỜI ĐIỂM VÀNG ĐỂ ĐẨY MẠNH KINH DOANH ONLINE
                    </Link>
                    <p className={styles.date}>
                      <FontAwesomeIcon icon={faCalendarAlt} /> 27-02-2021
                    </p>
                  </div>
                </Col>
              </Row>
            );
          }
        })}
      <Row className={styles.row}>
        <Col
          xs={12}
          sm={12}
          md={12}
          lg={12}
          className={`${styles.col} ${styles.newest}`}
        >
          <img
            src={require("../../img/images/banner-cut.png").default}
            alt=""
          />
          <div className={styles.content}>
            <Link
              to={`${route.path}/g-store-thoi-diem-vang-de-day-manh-kinh-doanh-online?id=1`}
            >
              G-STORE – THỜI ĐIỂM VÀNG ĐỂ ĐẨY MẠNH KINH DOANH ONLINE
            </Link>
            <p className={styles.date}>
              <FontAwesomeIcon icon={faCalendarAlt} /> 27-02-2021
            </p>
          </div>
        </Col>
      </Row>
      <Row className={styles.row}>
        <Col
          xs={12}
          sm={12}
          md={8}
          lg={8}
          className={`${styles.col} ${styles.column}`}
        >
          <div className={styles.news}>
            <img
              src={require("../../img/images/banner-cut.png").default}
              alt=""
            />
            <div className={styles.content}>
              <Link>BÁN HÀNG ĐA KÊNH – XU HƯỚNG MỚI CỦA BÁN LẺ HIỆN ĐẠI</Link>
              <p className={styles.date}>
                <FontAwesomeIcon icon={faCalendarAlt} /> 27-02-2021
              </p>
            </div>
          </div>
        </Col>
        <Col xs={12} sm={12} md={4} lg={4}>
          <BlogComponent />
          <RecentPost />
        </Col>
      </Row>
    </>
  );
};

export default NewestPost;
