import {createSlice, configureStore} from '@reduxjs/toolkit';

const initialStateLayout = {
    isClicked: false,
    isDownloaded: false,
}

const layoutSlice = createSlice({
    name: 'layout',
    initialState: initialStateLayout,
    reducers: {
        toggleLayout(state){
            state.isClicked = !state.isClicked
        },
        toggleDownload(state){
            state.isDownloaded = !state.isDownloaded
        }
    }
})

const initialStateSignIn = {
    isSignedIn: false
}
const signInSlice = createSlice({
    name: 'signin',
    initialState: initialStateSignIn,
    reducers: {
        SignIn(state){
            state.isSignedIn = true;
        },
        SignOut(state){
            state.isSignedIn = false;
        } 
    }
})
export const layoutActions = layoutSlice.actions;
const store = configureStore({
    reducer: {layout: layoutSlice.reducer, signin: signInSlice.reducer}
});

export default store;
