import React, {useEffect, useState} from 'react';
import styles from '../scss/LinkNavigation.module.scss';
import logo from '../../img/images/logo3.png';
import logoFixed from '../../img/images/logo-2.png';
import {Route} from 'react-router-dom';
import { Button } from '@material-ui/core';
import {Link} from 'react-router-dom';
import Overlay from 'react-overlays/esm/Overlay';
import { useSelector } from 'react-redux';
const link = ['Trang chủ', 'Tính năng', 'Giới Thiệu', 'Màn hình', 'Về chúng tôi', 'Tin tức', 'Blog'];
const tag = ['home', 'features', 'introduction', 'screens', 'about-us', 'news', 'blogs'];
const LinkNavigation = ({toggle, transform}) =>{
    const isCLicked = useSelector(state => state.layout.isCLicked);
    const [fixed, setFixed] = useState(false);
    useEffect(() =>{
        const scrollFunction = () =>{
            if(window.pageYOffset > 200){
                setFixed(true);
            }
            else{
                setFixed(false)
            }
        }
        window.addEventListener('scroll', scrollFunction)
    }, [])
    return(
        <nav className={`${styles['nav-bar']} ${fixed && styles.fixed}`}>
            <div className={styles.logo}>
                <img src={fixed ? logoFixed : logo} alt='logo'/>
            </div>
            <ul className={`${transform ? styles['nav-bar-block'] : ''}`}>
                {link.map((items, index) =>{
                    if(index === tag.length - 1){
                        return <li onClick={() => toggle()} key={index}>
                            <Link to='/blogs'>{items}</Link>
                        </li>
                    }
                    return <li onClick={() => toggle()} key={index}><a href={`#${tag[index]}`}>{items}</a></li>
                })}
                <Route path='/admin'>
               <Button variant='contained'>Dashboard</Button> 
               </Route>
            </ul>
            <div onClick={toggle} className={`${styles.hamburger} ${transform && styles['hamburger-transition']}`}>
                <div></div>
                <div></div>
                <div></div>
            </div>
            {transform && <Overlay layout={toggle}/>}
        </nav>
    )
}

export default LinkNavigation;
