import React from 'react';
import { Col, Row } from 'react-bootstrap';
import SearchBox from '../SearchBox/SearchBox';
import WrapperBlog from '../../layout/WrapperBlog/WrapperBlog';
import LayoutPost from '../LayoutPost/LayoutPost';
import usePagination from '../hook/use-pagination';
const SearchingPost = ({data}) =>{
    const {nextPage, prevPage, pages, currentPage, paginationGroup, changeThePage, ourPages} = usePagination(data, 3, 1);
    console.log(currentPage, ourPages);
    console.log(paginationGroup());
    return(
        <Row>
            <Col xs={12} sm={12} md={8} lg={8}>
                {pages().map(items =>{
                    return <LayoutPost key={items.id} title={items.title} content={items.content1}/>
                })}
                <ul>
                    <button onClick={prevPage} disabled={currentPage === 1}>Prev</button>
                    {paginationGroup().map(items =>{
                        return <li onClick={changeThePage} key={items}>{items}</li>
                    })}

                </ul>
            </Col>
            <Col xs={12} sm={12} md={4} lg={4}>
                <WrapperBlog>
                    <SearchBox/>
                </WrapperBlog>
            </Col>
        </Row>
    )
}

export default SearchingPost;
