import React from "react";
import styles from "./FieldSet.module.scss";
import TextArea from "./Input/TextArea";
import useInput from "../../../hook/use-input";
const FieldSet = () => {
  const {
    isValid: titleIsValid,
    isTouched: titleIsTouched,
    valueInput: valueTitle,
    inputIsTouchedHandler: titleIsTouchedHandler,
    changeInputHandler: titleChangeHandler,
    resetInputHandler: resetTitleHandler,
  } = useInput((value) => value.trim().length > 0);
  const {isValid: contentIsValid, } = useInput(value => value.trim().length > 0);
  return (
    <div className={styles.form}>
      <TextArea
        textField={{
          variant: "outlined",
          label: "Tiêu Đề",
          placeholder: "Tiêu đề trang",
          multiline: "multiline",
        }}
      />
      <TextArea
        textField={{
          label: "Nội Dung 1",
          placeholder: "Nội dung trong trang",
          multiline: "multiline",
          variant: "outlined",
        }}
      />
      <TextArea
        textField={{
          variant: "outlined",
          label: "Nội Dung 2",
          placeholder: "Nội dung trong trang",
          multiline: "multiline",
        }}
      />
    </div>
  );
};

export default FieldSet;
