import React, { useState } from "react";
import Layout from "../Layout/Layout";
import FieldSet from "./UI/FieldSet";
import { Col, Row } from "react-bootstrap";
import Submit from "./UI/Submit/Submit";
import Container from "../container/Container";
import useHttp from "../../hook/use-http";
import Loading from "../../../UI/Loading/Loading";
import styles from "./ChangingPost.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { changePostActions } from "../../redux-store/change-post";
const ChangingPost = () => {
  const [newImagesUploading, setNewImagesUploading] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const dispatch = useDispatch();
  const dataBlog = useSelector(state => state.post.information);
  const {
    fetchingDataHandler: uploadImagesHandler,
    data: dataImages,
    status: statusImages,
    error: errorImages,
  } = useHttp();
  const {
    fetchingDataHandler: uploadData,
    data: getDataHandler,
    status: statusUpload,
    error: errorUploading,
  } = useHttp();
  const information = useSelector((state) => state.post.information);

  const submitHandler = async (event) => {
    event.preventDefault();
    if (newImagesUploading.length !== 0) {
      const formData = new FormData();
      newImagesUploading.forEach((items) => {
        formData.append("photos", items);
      });
      try {
        const response = await fetch("http://localhost:3001/api/images", {
          method: "POST",
          body: formData,
          // headers: {'Content-Type': 'multipart/form-data'}
        });
        if (!response.ok) {
          throw new Error("error");
        }
        const data = await response.json();
        const newImages = data.map(items =>{
          return items.filename;
        })
        dispatch(changePostActions.updateImages(newImages));
      } catch (err) {
        console.log(err.message);
      }
    }
  };
  const setNewImages = (items) => {
    setNewImagesUploading(items);
  };
  return (
    <>
      {(statusUpload === "pending" || statusImages === "pending") && (
        <Loading className={styles.loading} />
      )}
      <Layout title="Chỉnh Sửa Bài Viết">
        <form onSubmit={submitHandler}>
          <Row>
            <Col xs={12} sm={12} md={12} lg={8}>
              <Container>
                <FieldSet setNewImages={setNewImages} />
              </Container>
            </Col>
            <Col xs={12} sm={12} md={12} lg={4}>
              <Container>
                <Submit />
              </Container>
            </Col>
          </Row>
        </form>
        {errorUploading && statusUpload === "error" && (
          <p>Có lỗi xảy ra, vui lòng thử lại</p>
        )}
      </Layout>
    </>
  );
};

export default ChangingPost;
