import React, { useEffect } from "react";
import Layout from "../Layout/Layout";
import Input from "../UI/Input/Input";
import styles from "./UserDetail.module.scss";
import { Row, Col } from "react-bootstrap";
import ToggleButton from "../ChangingPost/UI/ToggleButton/ToggleButton";
import { Button } from "@material-ui/core";
import Container from "../container/Container";
import Badge from "../Badge/Badge";
import { useParams } from "react-router-dom";
import Loading from "../../../UI/Loading/Loading";
import { userActions } from "../../redux-store/change-user";
import { useDispatch, useSelector } from "react-redux";
const UserDetail = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user.user);
  const status = useSelector((state) => state.user.status);
  const newPassword = useSelector(state => state.user.newPassword);
  const params = useParams();
  useEffect(() => {
    const fetchUser = async () => {
      try {
        dispatch(userActions.loadingFetching());
        const response = await fetch(
          `http://localhost:3001/api/get-user/${params.username}`
        );
        if (!response.ok) {
          throw new Error("failed to fetch");
        }
        const dataUser = await response.json();
        dispatch(userActions.getInformation(dataUser));
      } catch (err) {
        dispatch(userActions.errorFetching());
      }
    };
    fetchUser();
  }, [params.username, dispatch]);
  console.log(user);
  const changeUserNameHandler = (event) => {
      dispatch(userActions.changeUsername(event.target.value));
  };
  const changeEmailHandler = (event) => {
      dispatch(userActions.changeEmailHandler(event.target.value));
  };
  const changePasswordHandler = (event) => {
    dispatch(userActions.newPasswordHandler(event.target.value));
  };
  return (
    <Layout title="Chỉnh Sửa Người Dùng">
      {status === "pending" && <Loading className={styles.loading} />}
      {status === "success" && user && (
        <form>
          <Row>
            <Col xs={12} sm={12} md={8} lg={8}>
              <Container>
                <Input
                  className={styles.input}
                  label="Tên người dùng"
                  input={{
                    type: "text",
                    required: true,
                    id: "name",
                    max: "100",
                    value: user.name,
                    onChange: changeUserNameHandler,
                  }}
                />
                <Input
                  className={styles.input}
                  label="Email"
                  input={{
                    type: "email",
                    required: true,
                    id: "email",
                    value: user.username,
                    onChange: changeEmailHandler,
                  }}
                />
                <Input
                  className={styles.input}
                  label="Mật khẩu mới"
                  input={{
                    type: "password",
                    required: true,
                    id: "password",
                    min: "8",
                    onChange: changePasswordHandler,
                    placeholder:
                      "Mật khẩu mới (Nhập mật khẩu cũ nếu không thay đổi)",
                  }}
                />
              </Container>
            </Col>
            <Col className={styles.col} xs={12} sm={12} md={4} lg={4}>
              <Container>
                <p>Trạng thái tài khoản</p>
                <div className={styles["row__lock"]}>
                  <ToggleButton changeStatusHandler={() => dispatch(userActions.lockAccount())} />
                  <Badge />
                </div>
                <Button variant="contained" type="submit">
                  Xác nhận
                </Button>
              </Container>
            </Col>
          </Row>
        </form>
      )}
    </Layout>
  );
};

export default UserDetail;
