import React, {useState} from "react";
import Layout from "../../layout/Layout/Layout";
import classes from "./Banner.module.scss";
import Line from "./Line/Line";
const content = [
    {
        title: 'Mục tiêu mà G-STORE theo đuổi là gì',
        content: 'Với triết lý: MINH BẠCH, NHIỆT HUYẾT, TẬN TÂM, AN TOÀN. G-Store sẽ là sự tập hợp, kết nối các nguồn lực toàn cầu nhằm mục đích xây dựng các sản phẩm, dịch vụ với nền tảng công nghệ 4.0 tạo ra lợi ích cho cộng đồng và xã hội',
    },
    {
        title: 'Tầm nhìn & sứ mệnh của G-STORE là gì',

    }
]
const Banner = () => {
    const [isTouched, setIsTouched] = useState(false);
    const touchedHandler = () =>{
        setIsTouched(prevState => !prevState);
    }
  return (
    <Layout className={classes.container}>
      <h2>Về chúng tôi</h2>
      <p className={classes.title}>
        G-Store luôn nỗ lực hàng ngày nhằm mang lại những giá trị vượt bậc, sự
        tiện lợi, nhanh chóng cho mọi khách hàng, cộng đồng sử dụng ứng dụng G-Store<br/> ngay cả đến
        những khách hàng khó tính nhất.
      </p>
      <Layout className={classes['content-box']}>
          <Line isTouched={isTouched} onToggle={touchedHandler}/>
      </Layout>
    </Layout>
  );
};

export default Banner;
