import React from "react";
import Layout from "../../layout/Layout/Layout";
import styles from "./News.module.scss";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/swiper.min.css";
import "swiper/components/effect-coverflow/effect-coverflow.min.css";
import "swiper/components/pagination/pagination.min.css";
import SwiperCore, { EffectCoverflow, Pagination } from "swiper/core";
import ScreenData from "../ViewApp/ScreenData";
import classes from '../ViewApp/Screen.module.scss';
SwiperCore.use([EffectCoverflow, Pagination]);
const News = () => {
  const renderSlider = ScreenData.map((items, index) => {
    return (
      <SwiperSlide className={classes.images} key={index}>
        <img src={`${process.env.PUBLIC_URL}/Screen/${items}`} alt="" />
      </SwiperSlide>
    );
  });
  return (
    <Layout className={styles.container}>
      <h2>Tin tức</h2>
      <p>Điểm tin hàng tuần với G-STORE</p>
      <Swiper
        className={classes.slider}
        spaceBetween={30}
        autoplay={{
          delay: 5000,
        }}
        loop={true}
        breakpoints={{
          200: {
            slidesPerView: 2,
          },
          576: {
            slidesPerView: 3,
            spaceBetween: 10,
          },
          772: {
            slidesPerView: 4,
            spaceBetween: 20,
          },
          1200: {
            slidesPerView: 5,
            spaceBetween: 25,
          },
        }}
        navigation={true}
        pagination={{
          clickable: true,
        }}
        grabCursor
        onPaginationRender={(swiper, el) => {
          el.classList.add(styles.pagination);
        }}
      >
        {renderSlider}
      </Swiper>
    </Layout>
  );
};

export default News;
