import React, { useEffect } from "react";
import WrapperBlog from "../../layout/WrapperBlog/WrapperBlog";
import styles from "./RecentPost.module.scss";
import { Row, Col } from "react-bootstrap";
import p1 from "../../img/images/Application.png";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCalendar } from "@fortawesome/free-regular-svg-icons";
import useHttp from "../hook/use-http";
import Loading from "../../UI/Loading/Loading";
const RecentPost = () => {
  const { data, status, fetchingDataHandler } = useHttp();
  useEffect(() => {
    fetchingDataHandler({
      url: "http://localhost:3001/post/nearest",
    });
  }, [fetchingDataHandler]);
  return (
    <WrapperBlog className={styles.container}>
      <p>Bài viết gần đây</p>
      {
          <>
        status === 'pending' && <Loading/>
        <div className={styles["recent-post"]}>
          <Row className={styles.row}>
            <Col xs={4} sm={4} md={4} lg={4}>
              <img src={p1} alt="" />
            </Col>
            <Col xs={8} sm={8} md={8} lg={8}>
              <Link>
                G-STORE – THỜI ĐIỂM VÀNG ĐỂ ĐẨY MẠNH KINH DOANH ONLINE
              </Link>
              <p className={styles.date}>
                <FontAwesomeIcon icon={faCalendar} />
                <span>17-02-2021</span>
              </p>
            </Col>
          </Row>
          <Row className={styles.row}>
            <Col xs={4} sm={4} md={4} lg={4}>
              <img src={p1} alt="" />
            </Col>
            <Col xs={8} sm={8} md={8} lg={8}>
              <Link>
                G-STORE – THỜI ĐIỂM VÀNG ĐỂ ĐẨY MẠNH KINH DOANH ONLINE
              </Link>
              <p className={styles.date}>
                <FontAwesomeIcon icon={faCalendar} />
                <span>17-02-2021</span>
              </p>
            </Col>
          </Row>
        </div>
        </>
      }
    </WrapperBlog>
  );
};

export default RecentPost;
