import React, {useState} from "react";
import Layout from "../Layout/Layout";
import Container from "../helper/Container/Container";
import { Button, TextField } from "@material-ui/core";
import styles from "./FormPost.module.scss";
import useInput from "../../hook/use-input";
import Files from "./Files";
import useHttp from "../../hook/use-http";
import { CSSTransition } from "react-transition-group";
import Inform from "../ChangingPost/UI/Inform/Inform";
const FormPost = () => {
  const [getImages, setImages] = useState([]);
  const [isValidImages, setIsValidImages] = useState(true);
  const {
    isValid,
    isTouched,
    inputIsTouchedHandler,
    changeInputHandler,
    valueInput,
  } = useInput((value) => value.trim().length > 0);
  const {fetchingDataHandler: uploadingImages, data: dataUploading, status: statusUploading, error: errorUploading} = useHttp();
  const gettingFilesHandler = (files) =>{
    setImages(files);
    console.log(files);
  }
  const submitHandler = event =>{
    event.preventDefault();
    if(setImages.length < 3){
      setIsValidImages(false);
      return;
    }
  }
  return (
    <Layout title="Tạo Blog">
      <Container className={styles.container}>
        <form onSubmit={submitHandler} className={styles.form}>
          <label htmlFor="title">Tiêu đề bài viết</label>
          <TextField
            id="title"
            margin="normal"
            required={true}
            label="Tiêu đề"
            variant="outlined"
            multiline
            placeholder="Tiêu Đề"
            size="medium"
            fullWidth={true}
            value={valueInput}
            onChange={changeInputHandler}
            onBlur={inputIsTouchedHandler}
            error={!isValid && isTouched}
          />
          {!isValid && isTouched && <p className={styles.invalid}>Tiêu đề không được để trống</p>}
          <label htmlFor="body-1">Đoạn 1</label>
          <TextField
            id="body-1"
            margin="normal"
            label="Đoạn 1"
            variant="outlined"
            multiline
            placeholder="Nội Dung 1"
            size="medium"
            fullWidth={true}
          />
          <label htmlFor="body-2">Đoạn 2</label>
          <TextField
            id="body-2"
            margin="normal"
            label="Đoạn 2"
            variant="outlined"
            multiline
            placeholder="Nội Dung 2"
            size="medium"
            fullWidth={true}
          />
          <label htmlFor="body-3">Đoạn 3</label>
          <TextField
            id="body-3"
            margin="normal"
            label="Đoạn 3"
            variant="outlined"
            multiline
            placeholder="Nội Dung 3"
            size="medium"
            fullWidth={true}
          />
          <Files gettingFiles={gettingFilesHandler}/>
          <Button type='submit' className={styles.btn} variant='contained'>Đăng bài!</Button>
        </form>
        <CSSTransition in={!isValidImages} mountOnEnter unmountOnExit classNames='fade-light'>

          <Inform>

          </Inform>
        </CSSTransition>
      </Container>
    </Layout>
  );
};

export default FormPost;
